import React, { useEffect, useState } from 'react';
import '../components/css/UserList.css';

// Import the API functions
import { getUsers, deleteUser } from '../services/UserService'; // Adjust the path if necessary

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const data = await getUsers();
        setUsers(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toISOString().split('T')[0]; // Format to YYYY-MM-DD
  };

  const handleDelete = async (user) => { // Assuming user object is passed
    try {
      // Ensure user has an ID property
      if (!user.ID) {
        console.error("User object passed to handleDelete is missing ID property");
        return;
      }

      // Delete the user from the database
      await deleteUser(user.ID);

      // Filter out the deleted user from the users state
      const updatedUsers = users.filter(u => u.ID !== user.ID);

      // Update the users state with the remaining users
      setUsers(updatedUsers);
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div className="user-list-container">
      <h2>User List</h2>
      <table className="user-list-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Date of Birth</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={user.ID || index}>
              <td>{user.Name}</td>
              <td>{user.Email}</td>
              <td>{formatDate(user.DateOfBirth)}</td>
              <td>
                <button className="update-button">Update</button>
                <button 
                  className="delete-button"
                  onClick={() => handleDelete(user)} 
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserList;
